datastream = ""


def contains_repeat(input_string):
    if len(input_string) < 2:
        return False
    sorted_input = "".join(sorted(input_string))
    for contains_repeat_pos in range(1, len(sorted_input)):
        if sorted_input[contains_repeat_pos - 1] == sorted_input[contains_repeat_pos]:
            return True
    return False


assert not contains_repeat("")
assert not contains_repeat("a")
assert contains_repeat("aa")
assert not contains_repeat("ab")
assert contains_repeat("aabc")
assert contains_repeat("abbc")
assert contains_repeat("abcc")
assert contains_repeat("aaab")
assert contains_repeat("abbb")
assert not contains_repeat("abcd")
assert not contains_repeat("dbca")
assert contains_repeat("dbcb")

def find_first_nonrepeating_string(stream, length):
    if len(stream) < length:
        return None

    for possible_start_pos in range(0, len(stream) - length + 1):
        if not contains_repeat(stream[possible_start_pos : possible_start_pos+length]):
            return possible_start_pos + length

    return None


assert find_first_nonrepeating_string("", 1) is None
assert find_first_nonrepeating_string("abcd", 4) == 4
assert find_first_nonrepeating_string("abcd", 5) is None
assert find_first_nonrepeating_string("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 4) == 7
assert find_first_nonrepeating_string("bvwbjplbgvbhsrlpgdmjqwftvncz", 4) == 5
assert find_first_nonrepeating_string("nppdvjthqldpwncqszvftbrmjlhg", 4) == 6
assert find_first_nonrepeating_string("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 4) == 10
assert find_first_nonrepeating_string("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 4) == 11

print ("Part 1: start-of-packet detected after %d characters"  % find_first_nonrepeating_string(datastream, 4))



assert find_first_nonrepeating_string("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 14) ==19
assert find_first_nonrepeating_string("bvwbjplbgvbhsrlpgdmjqwftvncz", 14) == 23
assert find_first_nonrepeating_string("nppdvjthqldpwncqszvftbrmjlhg", 14) == 23
assert find_first_nonrepeating_string("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 14) == 29
assert find_first_nonrepeating_string("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 14) == 26

print ("Part 2: start-of-message detected after %d characters"  % find_first_nonrepeating_string(datastream, 14))
